import React, { useEffect, useState } from "react";
import "./Components.css";
import Course from "./Course.js";
import axios from "axios";

class CourseTable extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      courses: [],
      areas: [],
    };
  }

  componentDidMount() {
    axios.get("http://localhost:5000/courses").then((res) => {
      const courses = res.data;
      this.setState({ courses });
    });
  }

  renderSemester() {
    var semester = 7;
    var renderedSemesters = [];
    do {
      renderedSemesters.push(
        <div className={"period:" + semester}>
          <h2>Termin {semester}</h2>
          {this.renderAreas(semester)}
        </div>
      );
      semester++;
    } while (semester <= 10);
    return <div>{renderedSemesters}</div>;
  }

  async renderAreas(semester) {
    var all_areas = [];
    var renderedAreas = [];

    axios.get("http://localhost:5000/all_areas").then((res) => {
      all_areas = res.data;
    });

    console.log("areas:" + all_areas);

    for (let index = 0; index < all_areas.length; index++) {
      const area = all_areas[index];
      renderedAreas.push(
        <div className={"semester" + semester + "area" + area}>
          <h3>{area}</h3>
        </div>
      );
    }
  }

  renderNewPeriod(semester, area) {
    var period = 1;
  }

  renderCourse(semester, area, period) {}

  render() {
    var table_test = this.renderSemester();
    return (
      <div>
        {table_test}
        <table className="table table-sm">
          <thead>
            <tr>
              <th scope="col">Check</th>
              <th scope="col">Kurskod</th>
              <th scope="col">Kurs</th>
              <th scope="col">Hp</th>
              <th scope="col">Nivå</th>
              <th scope="col">Block</th>
              <th scope="col">VOF</th>
              <th scope="col">TEN</th>
              <th scope="col">LAB</th>
              <th scope="col">PRA</th>
              <th scope="col">UPG</th>
              <th scope="col">KTR</th>
              <th scope="col">HEM</th>
              <th scope="col">BAS</th>
            </tr>
          </thead>
        </table>
      </div>
    );
  }
}

export default CourseTable;
