import React, { useEffect, useState } from "react";


const Checkbox = (props) => {
  const [courseState, setCourseState] = useState(props.course);
  const dynamicValLength = courseState.dynamic_values.length;

  useEffect(() => {
    setCourseState(props.course);
  }, courseState.checked);

  console.log(dynamicValLength);

  return <p>{courseState.name}</p>;
}

