import React, { useEffect, useState } from "react";


const Checkbox = (props) => {
  const [courseState, setCourseState] = useState(props.course);
  const dynamicValLength = courseState.dynamic_values.length;

  useEffect(() => {
    setCourseState(props.course);
  }, courseState.checked);



  if (courseState.checked){
    for (let i = 0; i < dynamicValLength; i++) {
      const dynVal = courseState.dynamic_values[i];

      if (dynVal.semester === props.semester && dynVal.period === props.period && dynVal.checked_here){
        return (
          <div 
          class="mdc-checkbox" 
          style={{margin: "auto", border: "2px solid black"}}
          onClick={() => handleCheckboxClick(courseState, props.semester, props.period)}>
          <input type="checkbox"
                 class="mdc-checkbox__native-control"
                 id="checkbox-1"
                 checked
                 />
        </div>
        )
      }
      else{

      }
    }
  }

  return <p>{courseState.name}</p>;
}

handleCheckboxClick(() => {
  if (courseState.checked === true){
    setCourseState({...courseState, checked: false});
  }
  else{
    setCourseState({...courseState, checked: true});
  }

  for(let i = 0; i < dynamicValLength; i++){
    var dynVal = courseState.dynamic_values[i];
    if (dynVal.semester === props.semester && dynVal.period === props.period && dynVal.checked_here){
      setCourseState({...courseState, dynamic_values: [...courseState.dynamic_values, {...dynVal, checked_here: false}]});
    }
    if (dynVal.semester === props.semester && dynVal.period === props.period && !dynVal.checked_here){
      setCourseState({...courseState, dynamic_values: [...courseState.dynamic_values, {...dynVal, checked_here: true}]});
    }
  }
});

